import { reactive, ref, computed } from "vue";
import { useStore } from "vuex";
import ModelselectBox from "./components/ModelSelector.vue";
import DatasetselectBox from "./components/DatasetSelector.vue";
import HyperparameterselectBox from "./components/HyperparameterSelector.vue";
import NodeselectBox from "./components/NodeSelector.vue";
import axios from "axios";
export default {
  __name: 'Tables',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const store = useStore();
    const authToken = computed(() => store.state.authToken);
    const projectData = reactive({
      model: {},
      // Initially an object, later to be just a string of model_id
      dataset: {},
      // Initially an object, later to be just a string of dataset_id
      hyperparameters: {},
      // Already in the desired format
      nodes: [] // Initially an array of objects, later to be an array of node names
    });
    const isCreatingProject = ref(false);
    const prepareDataForSubmission = () => {
      return {
        model: projectData.model.model_id,
        // Just send the model name
        dataset: projectData.dataset.dataset_id,
        // Just send the dataset name
        hyperparameters: projectData.hyperparameters,
        // Already in the correct format
        nodes: projectData.nodes.map(node => node.name) // Convert nodes array to an array of names
      };
    };

    // Method to send data to backend
    const createProject = async () => {
      try {
        isCreatingProject.value = true;
        const formattedData = prepareDataForSubmission(); // 전달 데이터 형식 변경
        const config = {
          headers: {
            Authorization: `Bearer ${authToken.value}`
          }
        };
        const response = await axios.post("http://localhost:5000/api/create-project", formattedData, config);
        console.log(response.data);
        // Handle successful project creation (e.g., redirect or show message)
      } catch (error) {
        console.error("Failed to create project:", error);
        // Handle errors (e.g., show error message)
      } finally {
        isCreatingProject.value = false;
      }
    };
    const modelIdSelected = modelId => {
      if (modelId) {
        const fullModel = store.state.models.find(model => model.model_id === modelId);
        if (fullModel) {
          projectData.model = fullModel; // Assign the full model object
        } else {
          console.error("Model not found!");
        }
      }
    };
    const datasetSelected = datasetId => {
      projectData.dataset = {
        dataset_id: datasetId
      };
    };
    const __returned__ = {
      store,
      authToken,
      projectData,
      isCreatingProject,
      prepareDataForSubmission,
      createProject,
      modelIdSelected,
      datasetSelected,
      reactive,
      ref,
      computed,
      get useStore() {
        return useStore;
      },
      ModelselectBox,
      DatasetselectBox,
      HyperparameterselectBox,
      NodeselectBox,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
