import { ref, onMounted, watch } from "vue";
import Chart from "chart.js/auto";
import 'chartjs-plugin-datalabels';
export default {
  __name: 'GaugeComponent',
  props: {
    totalSize: Number,
    remainingSize: Number,
    nodeName: String
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;
    const chartCanvas = ref(null);
    let myChart = null;
    const createChartData = (totalSize, remainingSize) => {
      const usedSize = totalSize - remainingSize;
      return {
        labels: ["Used", "Free"],
        datasets: [{
          label: 'Memory Usage',
          data: [usedSize, remainingSize],
          backgroundColor: ["#42A5F5", "#ddd"],
          borderWidth: 0
        }]
      };
    };

    // Chart.js chart configuration
    const createChartConfig = (chartData, nodeName, totalSize) => {
      return {
        type: "doughnut",
        data: chartData,
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            animateRotate: true,
            animateScale: false
          },
          circumference: 180,
          rotation: 270,
          plugins: {
            tooltip: {
              enabled: false // Disable default tooltips
            },
            datalabels: {
              color: '#ff0000',
              // Red color for the percentage in the center
              textAlign: 'center',
              font: {
                weight: 'bold',
                size: 18 // Slightly larger font size for the percentage
              },
              formatter: (value, context) => {
                // Display the label only for the first dataset (Used memory)
                if (context.dataIndex === 0) {
                  const percentage = (value / totalSize * 100).toFixed(2) + '%';
                  return percentage;
                }
                return null; // Return null for other data to avoid duplication
              },
              // Position the percentage in the center of the doughnut chart
              anchor: 'center',
              align: 'center',
              offset: 0
            },
            title: {
              display: true,
              text: `${nodeName}`,
              position: 'top',
              color: '#666',
              font: {
                size: 16
              }
            }
          }
        },
        plugins: ['datalabels']
      };
    };
    onMounted(() => {
      const chartData = createChartData(props.totalSize, props.remainingSize);
      const config = createChartConfig(chartData, props.nodeName, props.totalSize);
      const context = chartCanvas.value.getContext('2d');
      myChart = new Chart(context, config);
    });
    watch(() => [props.totalSize, props.remainingSize], newValues => {
      const [totalSize, remainingSize] = newValues;
      const newChartData = createChartData(totalSize, remainingSize);
      if (myChart) {
        // Update each dataset's data
        myChart.data.datasets.forEach((dataset, index) => {
          dataset.data = newChartData.datasets[index].data;
        });

        // Update the percentage label formatter
        myChart.options.plugins.datalabels.formatter = (value, context) => {
          if (context.dataIndex === 0) {
            const percentage = (value / totalSize * 100).toFixed(2) + '%';
            return percentage;
          }
          return null;
        };

        // Update the chart without redrawing
        myChart.update({
          duration: 0,
          // Optional: Set animation duration to 0 to prevent animations
          lazy: false // Optional: Set lazy to false to force immediate update
        });
      }
    }, {
      immediate: true
    });
    const __returned__ = {
      props,
      chartCanvas,
      get myChart() {
        return myChart;
      },
      set myChart(v) {
        myChart = v;
      },
      createChartData,
      createChartConfig,
      ref,
      onMounted,
      watch,
      get Chart() {
        return Chart;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
