import { ref, onMounted, watch, computed } from "vue";
import Chart from "chart.js/auto";
import ChartDataLabels from "chartjs-plugin-datalabels";
export default {
  __name: 'GaugeComponent',
  props: {
    totalSize: Number,
    remainingSize: Number,
    nodeName: String
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;
    const chartCanvas = ref(null);
    let myChart = null;
    const createChartData = (totalSize, remainingSize) => {
      const usedSize = totalSize - remainingSize;
      return {
        labels: ["Used", "Free"],
        datasets: [{
          label: "Memory Usage",
          data: [usedSize, remainingSize],
          backgroundColor: ["#42A5F5", "#ddd"],
          borderWidth: 0
        }]
      };
    };
    const memoryUsageData = computed(() => createChartData(props.totalSize, props.remainingSize)); // Now using computed

    // Chart.js chart configuration
    const createChartConfig = (chartData, nodeName) => {
      const totalSize = chartData.datasets[0].data.reduce((a, b) => a + b, 0);
      return {
        type: "doughnut",
        data: chartData,
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            animateRotate: true,
            animateScale: false
          },
          circumference: 180,
          rotation: 270,
          plugins: {
            tooltip: {
              enabled: false // Disable default tooltips
            },
            datalabels: {
              color: "#ff0000",
              // Red color for the percentage in the center
              textAlign: "center",
              font: {
                weight: "bold",
                size: 18 // Slightly larger font size for the percentage
              },
              formatter: (value, context) => {
                // Display the label only for the first dataset (Used memory)
                if (context.dataIndex === 0) {
                  const percentage = (value / totalSize * 100).toFixed(2) + "%";
                  return percentage;
                }
                return null; // Return null for other data to avoid duplication
              },
              anchor: "center",
              align: "center",
              offset: 0
            },
            title: {
              display: true,
              text: nodeName,
              position: "top",
              color: "#666",
              font: {
                size: 16
              }
            }
          }
        },
        plugins: [ChartDataLabels] // Register the plugin here
      };
    };
    onMounted(() => {
      if (chartCanvas.value) {
        const context = chartCanvas.value.getContext('2d');
        if (context) {
          const config = createChartConfig(memoryUsageData.value, props.nodeName);
          myChart = new Chart(context, config);
        } else {
          console.error('Failed to get canvas context');
        }
      } else {
        console.error('Canvas element not found');
      }
    });
    watch(() => [props.totalSize, props.remainingSize], (newValues, oldValues) => {
      if (myChart) {
        const newChartData = createChartData(...newValues);
        myChart.data = newChartData;
        myChart.options = createChartConfig(newChartData, props.nodeName).options;
        myChart.update();
      }
    }, {
      immediate: true
    });
    const __returned__ = {
      props,
      chartCanvas,
      get myChart() {
        return myChart;
      },
      set myChart(v) {
        myChart = v;
      },
      createChartData,
      memoryUsageData,
      createChartConfig,
      ref,
      onMounted,
      watch,
      computed,
      get Chart() {
        return Chart;
      },
      get ChartDataLabels() {
        return ChartDataLabels;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
