import { ref, onMounted, watch } from "vue";
import Chart from "chart.js/auto";
import 'chartjs-plugin-datalabels';
export default {
  __name: 'GaugeComponent',
  props: {
    totalSize: Number,
    remainingSize: Number,
    nodeName: String
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;
    const chartCanvas = ref(null);
    let myChart = null;
    const createChartData = (totalSize, remainingSize) => {
      const usedSize = totalSize - remainingSize;
      return {
        labels: ["Used", "Free"],
        datasets: [{
          label: 'Memory Usage',
          data: [usedSize, remainingSize],
          backgroundColor: ["#42A5F5", "#ddd"],
          borderWidth: 0
        }]
      };
    };

    // Chart.js chart configuration
    const createChartConfig = chartData => {
      return {
        type: "doughnut",
        data: chartData,
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            animateRotate: true,
            animateScale: false
          },
          circumference: 180,
          rotation: 270,
          plugins: {
            tooltip: {
              enabled: false // Disable default tooltips
            },
            datalabels: {
              color: '#444',
              textAlign: 'center',
              font: {
                weight: 'bold',
                size: 16
              },
              formatter: (value, context) => {
                let label = context.chart.data.labels[context.dataIndex];
                label += "\n" + value + ' MB';
                return label;
              }
            },
            title: {
              display: true,
              text: `Memory Usage for ${props.nodeName}`,
              position: 'top'
            }
          }
        },
        plugins: ['datalabels']
      };
    };
    onMounted(() => {
      const chartData = createChartData(props.totalSize, props.remainingSize);
      const config = createChartConfig(chartData);
      const context = chartCanvas.value.getContext('2d');
      myChart = new Chart(context, config);
    });
    watch(() => [props.totalSize, props.remainingSize], newValues => {
      const [totalSize, remainingSize] = newValues;
      const newChartData = createChartData(totalSize, remainingSize);
      if (myChart) {
        myChart.data = newChartData;
        myChart.update();
      }
    }, {
      immediate: true
    });
    const __returned__ = {
      props,
      chartCanvas,
      get myChart() {
        return myChart;
      },
      set myChart(v) {
        myChart = v;
      },
      createChartData,
      createChartConfig,
      ref,
      onMounted,
      watch,
      get Chart() {
        return Chart;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
