import { ref, onMounted, watch, computed } from "vue";
import Chart from "chart.js/auto";
import ChartDataLabels from "chartjs-plugin-datalabels";
export default {
  __name: 'LineChartComponent',
  props: {
    nodeData: Object,
    dataType: String // Add a prop to specify the type of data
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;
    const chartCanvas = ref(null);
    let myChart = null;
    const createChartData = nodeData => {
      const datasets = Object.keys(nodeData).map((node, index) => ({
        label: `Node ${node}`,
        data: nodeData[node],
        borderColor: `hsl(${index * 135 % 360}, 70%, 50%)`,
        fill: false,
        pointRadius: 5 // Uniform radius for all points
      }));
      return {
        labels: Array.from({
          length: Math.max(...Object.values(nodeData).map(data => data.length))
        }, (_, i) => i + 1),
        datasets: datasets
      };
    };
    const lineChartData = computed(() => createChartData(props.nodeData));

    // Helper function to determine the correct unit
    const getUnit = dataType => {
      switch (dataType) {
        case "gpuTemperature":
          return "°C";
        case "cpuUtilization":
          return "%";
        case "gpuUtilization":
          return "%";
        case "gpuPowerUsage":
          return "W";
        default:
          return "";
      }
    };
    const createChartConfig = (chartData, dataType) => ({
      type: "line",
      data: chartData,
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          x: {
            title: {
              display: true,
              text: "Sample Index"
            }
          },
          y: {
            title: {
              display: true,
              text: "Value"
            }
          }
        },
        plugins: {
          tooltip: {
            mode: "index",
            intersect: false
          },
          datalabels: {
            color: "#444",
            display: "auto",
            font: {
              weight: "bold"
            },
            formatter: value => {
              const unit = getUnit(dataType);
              return `${value}${unit}`; // Now dynamically applying units
            }
          }
        }
      },
      plugins: [ChartDataLabels]
    });
    onMounted(() => {
      if (chartCanvas.value) {
        const context = chartCanvas.value.getContext("2d");
        if (context) {
          const config = createChartConfig(lineChartData.value, props.dataType);
          myChart = new Chart(context, config);
        } else {
          console.error("Failed to get canvas context");
        }
      } else {
        console.error("Canvas element not found");
      }
    });
    watch(() => [props.nodeData, props.dataType], newValues => {
      if (myChart) {
        const [newData, newType] = newValues;
        const newChartData = createChartData(newData);
        myChart.data = newChartData;
        myChart.options = createChartConfig(newChartData, newType).options;
        myChart.update();
      }
    }, {
      immediate: true
    });
    const __returned__ = {
      props,
      chartCanvas,
      get myChart() {
        return myChart;
      },
      set myChart(v) {
        myChart = v;
      },
      createChartData,
      lineChartData,
      getUnit,
      createChartConfig,
      ref,
      onMounted,
      watch,
      computed,
      get Chart() {
        return Chart;
      },
      get ChartDataLabels() {
        return ChartDataLabels;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
